package datatypes;

public class DataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte sum = 12;
		short product = 243;
		char gender = 'F';
		int amount = 3600;
		long total = 450000;
		float cost = 20.5f;
		double price = 734.678;
		boolean status = true;
		System.out.println("Byte: " + sum + "Short: " + product + "Char: " + gender + " Integer: " + amount + "Long: " + total + "Float: " + cost + "Double: " + price + "Boolean: " + status);

	}

}
